select * from orders

    and id = #{id}
    and user_id = #{userId}
    and status = #{status}

select o.* from orders o
left join order_detail od on o.id = o.id = od.order_id


        and o.order_time >= #{beginTime}


        and o.order_time <= #{endTime}


        and o.number like concat('%',#{number},'%')


        and o.phone = #{phone}


        and o.status = #{status}

SELECT
DATE(order_time) AS orderDate,
IFNULL(SUM(amount), 0.0) AS turnover
FROM orders
WHERE status = 5
AND order_time >= #{begin}
AND order_time <= #{end}
GROUP BY DATE(order_time);
